import React from 'react';
import { IMAGE_BASE_URL } from '../../../config';
import PropTypes from 'prop-types';
import './Actor.css';

const Actor = ({ actor }) => {

  const POSTER_SIZE = "w154";

  return (
    <div className="jmdb-actor">
      <img
        src={actor.profile_path ? `${IMAGE_BASE_URL}${POSTER_SIZE}${actor.profile_path}` : './images/no_image.jpg'}
        alt="actorthumb"
      />
      <span className="jmdb-actor-name">{actor.name}</span>
      <span className="jmdb-actor-character">{actor.character}</span>
    </div>
  )
}

Actor.propTypes = {
  actor: PropTypes.object
}

export default Actor;
