import React, { Component } from 'react';
import PropTypes from 'prop-types';
import FontAwesome from 'react-fontawesome';
import './SearchBar.css';

class SearchBar extends Component {
  state = {
    value: ''
  }
  timeout = null;

  doSearch = (event) => {
    const { callback } = this.props;

    this.setState({ value: event.target.value })
    clearTimeout(this.timeout);
    // Set a timeout to wait for the user to stop writing
    // So we don´t have to make unnessesary calls
    this.timeout = setTimeout(() => {
      callback(this.state.value);
    }, 500);
  }

  render() {
    // ES6 Destructuring state
    const { value } = this.state;

    return (
      <div className="jmdb-searchbar">
        <div className="jmdb-searchbar-content">
          <FontAwesome className="jmdb-fa-search" name="search" size="2x" />
          <input
            type="text"
            className="jmdb-searchbar-input"
            placeholder="Search"
            onChange={this.doSearch}
            value={value}
          />
        </div>
      </div>
    )
  }
}

SearchBar.propTypes = {
  callback: PropTypes.func
}

export default SearchBar;
